function generateGBKURL(keyword) {
  // ä½¿ç”¨Node.jsåŸç”Ÿæ–¹æ³•è¿›è¡ŒGBKç¼–ç 
  try {
    // åˆ›å»ºä¸´æ—¶çš„GBKç¼–ç æ˜ å°„
    const gbkMap = {
      'æ¸¯': '%B8%DB',
      'è´§': '%BB%F5',
      'é’¢': '%B8%D6',
      'åŒ–': '%BB%AF',
      'è†œ': '%C4%A4',
