        encoded += '%' + char.charCodeAt(0).toString(16).toUpperCase().padStart(2, '0');
      } else {
        // å¯¹äºå…¶ä»–å­—ç¬¦ï¼Œä½¿ç”¨åŸºæœ¬çš„UTF-8ç¼–ç ä½œä¸ºfallback
        const utf8Bytes = Buffer.from(char, 'utf8');
        for (const byte of utf8Bytes) {
          encoded += '%' + byte.toString(16).toUpperCase().padStart(2, '0');
        }
      }
    }

    return `https://s.1688.com/selloffer/offer_search.htm?keywords=${encoded}`;
  } catch (error) {
