config.script:
console.log('🔍 验证锚点高亮效果...');

// 检查是否有高亮元素
const overlays = document.querySelectorAll('.__webauto_anchor_overlay__');
const labels = document.querySelectorAll('[style*="position:fixed"][style*="background:#ff0000"]');
const outlined = document.querySelectorAll('[style*="outline"][style*="#ff0000"]');

console.log(`高亮覆盖层: ${overlays.length}`);
console.log(`标签元素: ${labels.length}`);
console.log(`轮廓元素: ${outlined.length}`);

// 创建验证结果面板
const resultPanel = document.createElement('div');
resultPanel.innerHTML = `
  <h2 style="color: #ff0000; margin: 0 0 10px 0;">🎯 锚点高亮验证结果</h2>
  <p><strong>高亮覆盖层:</strong> ${overlays.length} 个</p>
  <p><strong>标签元素:</strong> ${labels.length} 个</p>
  <p><strong>轮廓元素:</strong> ${outlined.length} 个</p>
  <p><strong>总计:</strong> ${overlays.length + labels.length + outlined.length} 个高亮元素</p>
  <hr style="margin: 10px 0;">
  <p style="color: #007aff;">如果看到红色高亮效果，说明锚点高亮功能正常！</p>
  <p style="color: #666; font-size: 11px;">高亮将保持30秒后自动消失</p>
`;

resultPanel.style.cssText = `
  position: fixed;
  top: 50px;
  right: 50px;
  background: white;
  border: 3px solid #ff0000;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 20px rgba(255, 0, 0, 0.5);
  z-index: 2147483647;
  font-family: Arial, sans-serif;
  font-size: 14px;
  min-width: 250px;
`;

document.body.appendChild(resultPanel);

// 30秒后移除面板
setTimeout(() => {
  try { resultPanel.remove(); } catch {}
}, 30000);

return {
  success: true,
  overlays: overlays.length,
  labels: labels.length,
  outlined: outlined.length,
  total: overlays.length + labels.length + outlined.length,
  timestamp: new Date().toISOString()
};
